mapper.insert:
INSERT /* com.greedy.jsp.member.model.dao.MemberDAO#insertMember() */
  INTO MEMBER A
(
  A.MEM_NO
, A.MEM_ID
, A.MEM_PWD
, A.MEM_NAME
, A.PHONE
, A.EMAIL
, A.ADDRESS
, A.MEMBER_ROLE
, A.BIRTH
, A.ENROLL_DATE
)
VALUES 
(
  SEQ_MEMBER_NO.NEXTVAL
, #{ id }
, #{ pwd }
, #{ memName }
, #{ phone }
, #{ email }
, #{ address }
, #{ role }
, #{ birth }
, #{ enrollDate }
)

INSERT
  INTO CLEANER_INFO A
(
  A.MEM_NO
, A.IDENTIFY_YN
, A.EDUCATE_YN
)
VALUES
(
  #{ memNo }
, #{ identifyYn }
, #{ educateYn }
)

mapper.select:
SELECT
	   MEM_ID
  FROM MEMBER A
 WHERE A.MEM_NAME = #{ memName }
   AND A.EMAIL = #{ email }
